import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View, Image } from 'react-native';

const mockItemData = {
  title: 'Margarita pizza',
  isNew: true, 
  image: require('./assets/pizza.png'),
  }

export default function App() {
  return (
    <View style={styles.container}>
      <View style={[styles.card, styles.innerBox]}>
        <View style={[styles.innerPhoto, styles.innerBox]}>
            {isNew && <Text style={styles.badge}>NEW</Text>}
            <Image
              style={styles.productPhoto}
              source={mockItemData.image}
              resizeMode="contain"
            />
        </View>
        <View style={styles.info}>
          <View style={styles.innerTitle}>
            <Text style={styles.title}>{mockItemData.title}</Text> 
            <Image
              style={styles.wishIcon}
              source={require('./assets/wishIcon.png')}
              resizeMode="contain"
            />
          </View>  
        </View>
      </View>
      <StatusBar style="auto" />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    paddingHorizontal: 10,
    backgroundColor: '#E0E0E0',
    alignItems: 'center',
    justifyContent: 'center',
  },
  innerBox: {
    borderRadius: 8,
    borderWidth: 1,
    borderColor: '#999999',
  },
  card: {
    flexDirection: 'row',
    width: '100%',
    columnGap: 30,
    paddingHorizontal: 10,
    paddingVertical: 20,
    backgroundColor: '#fff',
    marginVertical: 10,
    shadowColor: "#000",
    shadowOffset: {
        width: 0,
        height: 1,
    },
    shadowOpacity: 0.2,
    shadowRadius: 5,
  },
  innerPhoto: {
    width: 200,
    height: 200,
    paddingHorizontal: 15,
    paddingVertical: 15,
  },
  productPhoto: {
    width: '100%',
    height: '100%',
  },
  badge: {
    position: 'absolute',
    zIndex: 1,
    right: -15,
    top: -10,
    alignSelf: 'flex-end',
    backgroundColor: '#FF0000',
    borderWidth: 1,
    borderColor: '#922323',
    fontSize: 12,
    color: '#fff',
    paddingHorizontal: 10,
    paddingVertical: 10,
    borderRadius: '50%'
  },
  info: {
    flex: 1
  },
  innerTitle: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    columnGap: 30,
    marginBottom: 20
  },
  title: {
    fontSize: 35
  },
  wishIcon: {
    marginTop: 10,
    width: 30,
    height: 30
  }
});
