import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View, Image } from 'react-native';
import Card from './src/components/Card';

const mockItemData = {
  id: '1',
  title: 'Pizza capricciosa with mushrooms',
  description: 'Italian pizza made with baked ham, mushrooms, artichokes, tomatoes and mozzarella cheese. The mushrooms are usually mushrooms.',
  priceNew: '10',
  oldPrice: '15',
  isNew: true, 
  image: require('./assets/pizza.png'),
}
const { title, description, isNew, image, priceNew, oldPrice } = mockItemData;

export default function App() {
  return (
    <View style={styles.container}>
      <Card/>
      <StatusBar style="auto" />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    paddingHorizontal: 10,
    backgroundColor: '#E0E0E0',
    // alignItems: 'center',
    // justifyContent: 'center',
  },
  innerBox: {
    borderRadius: 8,
    borderWidth: 1,
    borderColor: '#999999',
  },
  card: {
    flexDirection: 'row',
    width: '100%',
    columnGap: 15,
    paddingHorizontal: 10,
    paddingVertical: 20,
    backgroundColor: '#fff',
    marginVertical: 10,
    shadowColor: "#000",
    shadowOffset: {
        width: 0,
        height: 1,
    },
    shadowOpacity: 0.4,
    shadowRadius: 5,
    elevation: 5

  },
  innerPhoto: {
    width: 110,
    paddingHorizontal: 5,
    paddingVertical: 5,
  },
  productPhoto: {
    width: '100%',
    height: '100%',
  },
  badge: {
    position: 'absolute',
    zIndex: 1,
    right: -10,
    top: -10,
    alignSelf: 'flex-end',
    backgroundColor: '#FF0000',
    borderWidth: 1,
    borderColor: '#922323',
    fontSize: 11,
    color: '#fff',
    paddingHorizontal: 5,
    paddingVertical: 5,
    borderRadius: '50%'
  },
  info: {
    flex: 1
  },
  title: {
    fontSize: 22,
    lineHeight: '1',
    marginBottom: 15
  },
  innerPrice: {
    flexDirection: 'row', 
    alignItems: 'center',
    columnGap: 25,
    marginBottom: 15
  },
  price: {
    fontSize: 22,
    lineHeight: '1',
  },
  newPrice: {
    fontWeight: 600
  },
  oldPrice: { 
    textDecorationLine: 'line-through'
  },
  actions: {
    justifyContent: 'space-between',
    alignItems: 'end',
  },
  wishIcon: {
    width: 30,
    height: 30
  },
  innerCart: {
    flexDirection: 'row',
    alignItems: 'end',
    columnGap: 4 
  },
  cartText: {
    fontSize: 16,
    fontWeight: 600
  },
  cartIcon: {
    width: 30,
    height: 30,
    marginBottom: -5
  }
});
