import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View, Image } from 'react-native';

const mockItemData = {
  title: 'Pizza 1',
  isNew: true, 
  image: require('./assets/pizza.png'),
  }

export default function App() {
  return (
    <View style={styles.container}>
      <View style={[styles.card, styles.innerBox]}>
        <View style={[styles.innerPhoto, styles.innerBox]}>
            <Text style={styles.badge}>NEW</Text>
            <Image
              style={styles.productPhoto}
              source={mockItemData.image}
              resizeMode="contain"
            />
        </View>
        <View></View>
      </View>
      <StatusBar style="auto" />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    paddingHorizontal: 10,
    backgroundColor: '#E0E0E0',
    alignItems: 'center',
    justifyContent: 'center',
  },
  innerBox: {
    borderRadius: 8,
    borderWidth: 1,
    borderColor: '#999999',
  },
  card: {
    width: '100%',
    paddingHorizontal: 10,
    paddingVertical: 10,
    borderWidth: 1,
    backgroundColor: '#fff',
    marginVertical: 10,
    shadowColor: "#000",
    shadowOffset: {
        width: 0,
        height: 1,
    },
    shadowOpacity: 0.2,
    shadowRadius: 5,
  },
  innerPhoto: {
    width: 200,
    height: 200,
    paddingHorizontal: 15,
    paddingVertical: 15,
  },
  productPhoto: {
    width: '100%',
    height: '100%',
  },
  badge: {
    alignSelf: 'flex-end',
    backgroundColor: 'red'
  }


});
