import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View, Image } from 'react-native';

const mockItemData = {
  title: 'Pizza 1',
  isNew: true, 
  image: require('./assets/icon.png'),
  }

export default function App() {
  return (
    <View style={styles.container}>
      <View style={[styles.card, styles.innerBox]}>
        <View style={[styles.innerPhoto, styles.innerBox]}>
            <Image
              style={[styles.productPhoto, styles.innerBox]}
              source={mockItemData.image}
            />
        </View>
        <View></View>
      </View>
      <StatusBar style="auto" />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    paddingHorizontal: 10,
    backgroundColor: '#E0E0E0',
    alignItems: 'center',
    justifyContent: 'center',
  },
  card: {
    width: '100%',
    borderWidth: 1,
    backgroundColor: '#fff',
    marginVertical: 10,
    shadowColor: "#000",
    paddingHorizontal: 10,
    paddingVertical: 10,
    shadowOffset: {
        width: 0,
        height: 1,
    },
    shadowOpacity: 0.2,
    shadowRadius: 5,
  },
  innerBox: {
    borderRadius: 8,
    borderWidth: 1,
    borderColor: '#999999',
  },
  innerPhoto: {
    width: 160,
    height: 160,
  },
  productPhoto: {
    width: 150,
    height: 150,
  }

});
